function initEZS(e,t,n){var r=parseInt(t);var i=parseInt(n);var o=$(".ezs").toArray();var u=[];for(var a=0;a<o.length;a++){$(o[a]).css("position","relative");$(o[a]).css("z-index","100000");u.push($(o[a]).offset().top)}$(window).scroll(function(){var t=document.documentElement,n=document.body;var s=t&&t.scrollTop||n&&n.scrollTop||0;for(var a=0;a<o.length;a++){if(e=="on"){if(s>=u[a]-i&&s<=u[a]+r-i){$(o[a]).css("position","fixed");$(o[a]).css("top",i+"px")}else if(s>=u[a]+r-i){$(o[a]).css("position","relative");$(o[a]).css("top",s-s+r+"px")}else{$(o[a]).css("position","relative");$(o[a]).css("top","0px")}}else{if(s>=u[a]-i){$(o[a]).css("position","fixed");$(o[a]).css("top",i+"px")}else{$(o[a]).css("position","absolute");$(o[a]).css("top","0px")}}}})}
